import fs from 'fs'

export default (req, res) => {
    // res.statusCode = 200
    // res.setHeader('Content-Type', 'application/json')
    // res.end(JSON.stringify({ name: 'John Doe' }))

    fs.readFile('/Users/lsmienk/status.txt', 'utf8', function(err, data) {
        const packages = data.split('\n\n');
        const result = packages.reduce((acc, current) => {
            const pairs = current.split(/(?<=^|[\r\n])([A-Z][^:]*): ([^\r\n]*(?:[\r\n]+(?![A-Z].*:).*)*)/);
            let i = 0;
            const obj = {};
            while(pairs[i] !== undefined) {
                if (pairs[i] == '' || '\n') {
                    i++;
                    obj[pairs[i++]] = pairs[i++];
                }
            }
            acc.push(obj);
            return acc;
        }, []);

        res.end(JSON.parse(result));
    })
}
