import React from 'react'
import Head from 'next/head'
import Nav from '../components/nav'
import fetch from 'isomorphic-unfetch'

const Home = ({ packages }) => (
  <div>
    <Head>
      <title>Package Explorer</title>
      <link rel="icon" href="/favicon.ico" />
    </Head>
    {console.log(packages)}
    <Nav />
    {packages && packages
        .map((item, key) => {
          const list = Object.keys(item)
            .map((key) => (<h5>{key}: {item[key]}</h5>));
          return (
            <>
              <h1> --- Section: {item.Package}</h1>
              {list}
            </>
          );
        })}
  </div>
)

Home.getInitialProps = async () => {
  // TODO: create api endpoint reading file and structering data: https://nextjs.org/docs/api-routes/introduction
  // TODO: Parse text file to object (Google: Parsing text file to object in javascript)
  // TODO: Fetch API endpoint here, sent to client: https://nextjs.org/docs/api-reference/data-fetching/getInitialProps
  // fs.readFile('/Users/lsmienk/status.txt', 'utf8', function(err, contents) {
  //   console.log('content', contents);
  // });
  const res = await fetch('http://localhost:3000/api/packages')
  const json = await res.json();
  return { packages: json }
}

export default Home
