import Head from 'next/head'
import Nav from '../components/nav'
import fetch from 'isomorphic-unfetch'

const Home = ({ packages }) => (
  <div>
    <Head>
      <title>Package Explorer</title>
      <link rel="icon" href="/favicon.ico" />
    </Head>
    {console.log(packages)}
    <Nav />
    {packages && packages
        .map((item, key) => {
          return (
            <>
              <h1> --- {item.Package} --- </h1>
            </>
          );
        })}
  </div>
)

