import React from 'react'
import Head from 'next/head'
import Nav from '../components/nav'
import fetch from 'isomorphic-unfetch'

const Home = ({ packages }) => (
  <div>
    <Head>
      <title>Package Explorer</title>
      <link rel="icon" href="/favicon.ico" />
    </Head>

    <Nav />
    {packages && packages
        .map((item, key) => (<h1>{key}: {item.Package}</h1>))}
    <div className="hero">
      <h1 className="title">Package Explorer</h1>
      <p className="description">
      Write a small application that displays some key information about currently installed packages via a web interface.
      </p>

      <div className="row">
        <a href="https://nextjs.org/docs" className="card">
          <h3>Documentation &rarr;</h3>
          <p>Learn more about Next.js in the documentation.</p>
        </a>
        <a href="https://nextjs.org/learn" className="card">
          <h3>Next.js Learn &rarr;</h3>
          <p>Learn about Next.js by following an interactive tutorial!</p>
        </a>
        <a
          href="https://github.com/zeit/next.js/tree/master/examples"
          className="card"
        >
          <h3>Examples &rarr;</h3>
          <p>Find other example boilerplates on the Next.js GitHub.</p>
        </a>
      </div>
    </div>

    <style jsx>{`
      .hero {
        width: 100%;
        color: #333;
      }
      .title {
        margin: 0;
        width: 100%;
        padding-top: 100px;
        line-height: 1.15;
        font-size: 48px;
      }
      .title,
      .description {
        text-align: center;
      }
      .row {
        max-width: 880px;
        margin: 80px auto 40px;
        display: flex;
        flex-direction: row;
        justify-content: space-around;
      }
      .card {
        padding: 18px 18px 24px;
        width: 220px;
        text-align: left;
        text-decoration: none;
        color: #434343;
        border: 1px solid #9b9b9b;
      }
      .card:hover {
        border-color: #067df7;
      }
      .card h3 {
        margin: 0;
        color: #067df7;
        font-size: 18px;
      }
      .card p {
        margin: 0;
        padding: 12px 0 0;
        font-size: 13px;
        color: #333;
      }
    `}</style>
  </div>
)

Home.getInitialProps = async () => {
  // TODO: create api endpoint reading file and structering data: https://nextjs.org/docs/api-routes/introduction
  // TODO: Parse text file to object (Google: Parsing text file to object in javascript)
  // TODO: Fetch API endpoint here, sent to client: https://nextjs.org/docs/api-reference/data-fetching/getInitialProps
  // fs.readFile('/Users/lsmienk/status.txt', 'utf8', function(err, contents) {
  //   console.log('content', contents);
  // });
  const res = await fetch('http://localhost:3000/api/packages')
  const json = await res.json();
  return { packages: json }
}

export default Home
